package capgeminibasicsjava;

public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
double x=2.34;
 int y;  //narowing
 y=(int )x;
 System.out.println(y);
 
 
 int z=2;  //widening
 double a;
 a=z;
 System.out.println(a);
	}

}
